        useEffect(()=>{
            axios({method:"GET",url:apiurl.params.cakeid,data:JSON}).then((response)=>{
                console.log("propcale..",response.data.data)
                setData(response.data.data)
            setLodding(false)
            },(error)=>{
                console.log("error..",error.data.data)
                setLodding(false)
            });

})
    return(
        <>
