import './component.css';
import {Component} from "react";
class Signup extends Component {

constructor(){
    super()
    this.state = {

        input: {},

        errors: {}

      };
    }
      handleChange(event) {

        let input = this.state.input;

        input[event.target.name] = event.target.value;

      if(this.validate()){
        let input = {};

        input["name"] = "";

        input["email"] = "";

        input["comment"] = "";

        this.setState({input:input});
      }

        this.setState({

          input

        });

      }
      validate(){
        let input = this.state.input;

        let errors = {};

        let isValid = true;
        if (typeof input["email"] !== "undefined") {



            var pattern = new RegExp(/^(("[\w-\s]+")|([\w-]+(?:\.[\w-]+)*)|("[\w-\s]+")([\w-]+(?:\.[\w-]+)*))(@((?:[\w-]+\.)*\w[\w-]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$)|(@\[?((25[0-5]\.|2[0-4][0-9]\.|1[0-9]{2}\.|[0-9]{1,2}\.))((25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\.){2}(25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\]?$)/i);

            if (!pattern.test(input["email"])) {

              isValid = false;

              errors["email"] = "Please enter valid email address.";

            }

          }
      }


     render(){
         return(
            <div className="container signup ">
            <div className="row justify-content-center">
            <div className="col-md-5">
             <div className="card">
               <h2 className="card-title text-center">Register</h2>
                <div className="card-body py-md-4">
                 <form _lpchecked="1">
                    <div className="form-group">
                       <input type="text" className="signup form-control" id="name" placeholder="Name"/>
                  </div>
                  <div className="form-group">
                       <input type="text" className="signup form-control" id="email"  value={this.state.input.email}  onChange={this.handleChange} placeholder="Email"/>
                       <p class="help-block help-block-error">{this.state.errors.email}</p>
                                      </div>


             <div className="form-group">
               <input type="password" className="signup form-control" id="password" placeholder="Password"/>
             </div>
             <div className="form-group">
                <input type="password" className="signup form-control" id="confirm-password" placeholder="confirm-password"/>
             </div>
             <div className="d-flex flex-row align-items-center justify-content-between">
            <button className=" signup btn btn-primary" >Create Account</button>
                    </div>
                 </form>
               </div>
            </div>
          </div>
          </div>
          </div>
         )
     }
}

export default Signup
