import { useParams } from "react-router"
import "./component.css";

import axios from "axios";
import { useEffect, useState } from 'react';


function Carddetails(props){
var params = useParams(props)

var apiurl=`http://apibyashu.herokuapp.com/api/cake/ ${params.cakeid}`
var cakeid = params.cakeid
var [data,setData]=useState([]);
var [islodding,setLodding]=useState(true)
        useEffect(()=>{
            axios({method:"GET",url:apiurl,data:JSON}).then((response)=>{
                console.log("response",response.data.data)
                setData(response.data.data)
                setLodding(false)
            },(error)=>{

                console.log("error..",error)
                setLodding(false)
            });

},islodding)
    return(
        <>
  <div class="card mb-3 ">
  <img class="card-img-left" style = {{"height":"340px","width":"498px"}} src={data.image} alt="Card image cap"/>
  <div class="card-body">
    <h5 class="card-title">{data.name}</h5>
    <p class="card-text">This is a wider card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.</p>
    <p class="card-text"><small class="text-muted">Last updated 3 mins ago</small></p>
  </div>
</div>
        </>

    )
}
export default Carddetails
